import { DuaItem } from "./types";

export const propheticDuas: DuaItem[] = [
    { id: 201, category: "prophetic", arabic: "اللهم صلِّ على محمدٍ وعلى آل محمدٍ كما صليتَ على آل إبراهيم؛ إنك حميد مجيد، اللهم بارك على محمدٍ وعلى آل محمدٍ كما باركتَ على آل إبراهيم؛ إنك حميد مجيد", transliteration: "Allahumma salli 'ala Muhammadin wa 'ala ali Muhammadin kama sallayta 'ala ali Ibrahima innaka Hamidun Majid. Allahumma barik 'ala Muhammadin wa 'ala ali Muhammadin kama barakta 'ala ali Ibrahima innaka Hamidun Majid.", translation: "O Allah, send prayers upon Muhammad and upon the family of Muhammad, as You sent prayers upon the family of Abraham. Indeed, You are Praiseworthy, Most Glorious. O Allah, send blessings upon Muhammad and upon the family of Muhammad, as You sent blessings upon the family of Abraham. Indeed, You are Praiseworthy, Most Glorious.", source: "(Al-Bukhari: 6357)" },
    { id: 202, category: "prophetic", arabic: "اللهم إني ظلمتُ نفسي ظلمًا كثيرًا، ولا يغفر الذنوب إلا أنت؛ فاغفر لي مغفرةً من عندك وارحمني؛ إنك أنت الغفور الرحيم", transliteration: "Allahumma inni zalamtu nafsi zulman kathiran wa la yaghfirudh-dhunuba illa Ant. Faghfir li maghfiratan min 'indika warhamni innaka Antal-Ghafurur-Rahim.", translation: "O Allah, I have wronged myself greatly, and no one forgives sins except You. So forgive me with a forgiveness from You, and have mercy on me. Indeed, You are the Forgiving, the Merciful.", source: "(Al-Bukhari: 834, 6326)" },
    { id: 203, category: "prophetic", arabic: "اللهم أغِثْنا، اللهم أغثنا، اللهم أغثنا", transliteration: "Allahumma aghithna, Allahumma aghithna, Allahumma aghithna.", translation: "O Allah, relieve us! O Allah, relieve us! O Allah, relieve us!", source: "(Al-Bukhari: 1014)" },
    { id: 204, category: "prophetic", arabic: "اللهم إني أعوذ بك من عذاب القبر، ومن عذاب النار، ومن فتنة المحيا والممات، ومن فتنة المسيح الدجال", transliteration: "Allahumma inni a'udhu bika min 'adhabil-qabr, wa min 'adhabin-nar, wa min fitnatil-mahya wal-mamat, wa min fitnatil-masihid-dajjal.", translation: "O Allah, I seek refuge in You from the punishment of the grave, and from the punishment of the Fire, and from the trials of life and death, and from the trial of the False Messiah.", source: "(Al-Bukhari: 1377)" },
    { id: 205, category: "prophetic", arabic: "اللهم إنك عفوٌّ تحب العفو؛ فاعفُ عني", transliteration: "Allahumma innaka 'Afuwwun tuhibbul-'afwa fa'fu 'anni.", translation: "O Allah, You are Forgiving and love forgiveness, so forgive me.", source: "(Al-Bukhari: 3294)" },
    { id: 206, category: "prophetic", arabic: "اللهم أنجِ المستضعفين من المؤمنين", transliteration: "Allahumma anjil-mustad'afina minal-mu'minin.", translation: "O Allah, save the weak among the believers.", source: "(Al-Bukhari: 2932)" },
    { id: 207, category: "prophetic", arabic: "يا رب، لا غنَى لي عن بركتك", transliteration: "Ya Rabbi la ghina li 'an barakatik.", translation: "O Lord, I am not in need of anything but Your blessing.", source: "(Al-Bukhari: 3391)" },
    { id: 208, category: "prophetic", arabic: "اللهم أحيِني ما كانت الحياة خيرًا لي، وتوفَّني إذا كانت الوفاة خيرًا لي", transliteration: "Allahumma ahyini ma kanatil-hayatu khayran li, wa tawaffani idha kanatil-wafatu khayran li.", translation: "O Allah, keep me alive as long as life is good for me, and cause me to die if death is better for me.", source: "(Al-Bukhari: 5671)" },
    { id: 209, category: "prophetic", arabic: "اللهم أنت ربي لا إله إلا أنت، خلقتَني وأنا عبدك، وأنا على عهدك ووعدك ما استطعت، أعوذ بك من شرِّ ما صنعتُ، أبوء لك بنعمتك عليَّ، وأبوء لك بذنبي فاغفر لي؛ فإنه لا يغفر الذنوب إلا أنت", transliteration: "Allahumma Anta Rabbi la ilaha illa Ant. Khalaqtani wa ana 'abduk, wa ana 'ala 'ahdika wa wa'dika mastata't. A'udhu bika min sharri ma sana't. Abu'u laka bini'matika 'alayya wa abu'u laka bidhanbi faghfir li fa-innahu la yaghfirudh-dhunuba illa Ant.", translation: "O Allah, You are my Lord, there is no god but You. You created me and I am Your slave, and I am faithful to my covenant and my promise (to You) as much as I can. I seek refuge in You from all the evil I have done. I acknowledge before You all the blessings You have bestowed upon me, and I confess to You all my sins. So I entreat You to forgive me, for no one can forgive sins except You.", source: "(Al-Bukhari: 6306)" },
    { id: 210, category: "prophetic", arabic: "اللهم اجعل في قلبي نورًا، وفي بصري نورًا، وفي سمعي نورًا، وعن يميني نورًا، وعن يساري نورًا، وفوقي نورًا، وتحتي نورًا، وأمامي نورًا، وخلفي نورًا، واجعل لي نورًا", transliteration: "Allahummaj'al fi qalbi nura, wa fi basari nura, wa fi sam'i nura, wa 'an yamini nura, wa 'an yasari nura, wa fawqi nura, wa tahti nura, wa amami nura, wa khalfi nura, waj'al li nura.", translation: "O Allah, place light in my heart, and on my tongue light, and in my ears light and in my sight light, and above me light, and below me light, and to my right light, and to my left light, and before me light and behind me light. Place in my soul light. Magnify for me light, and amplify for me light. Make for me light, and make me light. O Allah, grant me light, and place light in my nerves, and in my body light and in my blood light and in my hair light and in my skin light.", source: "(Al-Bukhari: 6316)" },
    { id: 211, category: "prophetic", arabic: "اللهم إني أعوذ بك من الهمِّ والحزَن، والعجز والكسل، والبخل والجبن، وضَلَع الدَّين، وغلَبة الرجال", transliteration: "Allahumma inni a'udhu bika minal-hammi wal-hazan, wal-'ajzi wal-kasal, wal-bukhli wal-jubn, wa dala'id-dayni wa ghalabatir-rijal.", translation: "O Allah, I seek refuge in You from anxiety and sorrow, weakness and laziness, miserliness and cowardice, the burden of debts and from being overpowered by men.", source: "(Al-Bukhari: 6363)" },
    { id: 212, category: "prophetic", arabic: "الل اللهم اغفِرْ لي خطيئتي وجهلي، وإسرافي في أمري، وما أنت أعلمُ به مني", transliteration: "Allahummaghfir li khati'ati wa jahli, wa israfi fi amri, wa ma Anta a'lamu bihi minni.", translation: "O Allah, forgive my errors, my ignorance, and my excess in my affairs, and what You know better than me.", source: "(Al-Bukhari: 6399)" },
    { id: 213, category: "prophetic", arabic: "اللهم إني أعوذ بك مِن جَهد البلاء، ودرَك الشقاء، وسوء القضاء، وشماتة الأعداء", transliteration: "Allahumma inni a'udhu bika min jahdil-bala', wa darakish-shaqa', wa su'il-qada', wa shamatatil-a'da'.", translation: "O Allah, I seek refuge in You from the severe calamity, and from being overtaken by misery, and from a bad destiny, and from the gloating of enemies.", source: "(Al-Bukhari: 6347)" },
    { id: 214, category: "prophetic", arabic: "أتوب إلى الله مما أذنبتُ", transliteration: "Atubu ilallahi mimma adhnabt.", translation: "I repent to Allah from what I have sinned.", source: "(Al-Bukhari: 5957)" },
    { id: 215, category: "prophetic", arabic: "اللهم اغفر لي ذنبي كلَّه؛ دِقَّه وجِلَّه، وأوله وآخره، وعلانيته وسره", transliteration: "Allahummaghfir li dhanbi kullah, diqqahu wa jillah, wa awwalahu wa akhirah, wa 'alaniyatahu wa sirrah.", translation: "O Allah, forgive me all my sins, great and small, the first and the last, the open and the secret.", source: "(Muslim: 483)" },
    { id: 216, category: "prophetic", arabic: "اللهم باعِدْ بيني وبين خطاياي كما باعدتَ بين المشرق والمغرب، اللهم نقِّني من خطاياي كما ينقى الثوب الأبيض مِن الدَّنس، اللهم اغسِلْني من خطاياي بالثلج والماء والبَرَد", transliteration: "Allahumma ba'id bayni wa bayna khatayaya kama ba'adta baynal-mashriqi wal-maghrib. Allahumma naqqini min khatayaya kama yunaqqath-thawbul-abyadu minad-danas. Allahummaghsilni min khatayaya bith-thalji wal-ma'i wal-barad.", translation: "O Allah, distance me from my sins as You have distanced the East from the West. O Allah, purify me from my sins as a white garment is purified from dirt. O Allah, wash me from my sins with snow, water, and hail.", source: "(Muslim: 598)" },
    { id: 217, category: "prophetic", arabic: "اللهم بارِكْ لهم فيما رزقتهم، واغفر لهم وارحمهم", transliteration: "Allahumma barik lahum fima razaqtahum, waghfir lahum warhamhum.", translation: "O Allah, bless them in what You have provided for them, and forgive them and have mercy on them.", source: "(Muslim: 2042)" },
    { id: 218, category: "prophetic", arabic: "اللهم أطعم مَن أطعمني، وأسقِ مَن أسقاني", transliteration: "Allahumma at'im man at'amani, wasqi man asqani.", translation: "O Allah, feed the one who has fed me, and give drink to the one who has given me drink.", source: "(Muslim: 2055)" },
    { id: 219, category: "prophetic", arabic: "اللهم مصرِّف القلوب، صرِّف قلوبنا على طاعتك", transliteration: "Allahumma Musarrifal-qulub, sarrif qulubana 'ala ta'atik.", translation: "O Allah, Turner of the hearts, turn our hearts to Your obedience.", source: "(Muslim: 2654)" },
    { id: 220, category: "prophetic", arabic: "اللهم اغفر لي، وارحمني، واهدِني، وعافِني وارزقني", transliteration: "Allahummaghfir li, warhamni, wahdini, wa 'afini warzuqni.", translation: "O Allah, forgive me, have mercy on me, guide me, heal me, and provide for me.", source: "(Muslim: 2697)" },
    { id: 221, category: "prophetic", arabic: "اللهم إني أعوذ بك من شرِّ ما عملتُ، ومن شر ما لم أعمل", transliteration: "Allahumma inni a'udhu bika min sharri ma 'amiltu, wa min sharri ma lam a'mal.", translation: "O Allah, I seek refuge in You from the evil of what I have done, and from the evil of what I have not done.", source: "(Muslim: 2716)" },
    { id: 222, category: "prophetic", arabic: "اللهم أصلِحْ لي دِيني الذي هو عصمة أمري، وأصلح لي دنياي التي فيها معاشي، وأصلح لي آخرتي التي فيها معادي، واجعل الحياة زيادةً لي في كل خيرٍ، واجعل الموتَ راحةً لي من كل شر", transliteration: "Allahumma aslih li diniyal-ladhi huwa 'ismatu amri, wa aslih li dunyaya-llati fiha ma'ashi, wa aslih li akhirati-llati fiha ma'adi, waj'alil-hayata ziyadatan li fi kulli khayr, waj'alil-mawta rahatan li min kulli sharr.", translation: "O Allah, set right for me my religion which is the safeguard of my affairs. And set right for me my world which is the source of my livelihood. And set right for me my Hereafter to which is my return. And make life for me an increase in every good, and make death for me a relief from every evil.", source: "(Muslim: 2720)" },
    { id: 223, category: "prophetic", arabic: "اللهم إني أسألك الهدى والتقى، والعفاف والغنى", transliteration: "Allahumma inni as'alukal-huda wat-tuqa, wal-'afafa wal-ghina.", translation: "O Allah, I ask You for guidance, piety, chastity, and self-sufficiency.", source: "(Muslim: 2721)" },
    { id: 224, category: "prophetic", arabic: "اللهم آتِ نفسي تقواها، وزكِّها أنت خيرُ مَن زكاها، أنت وليُّها ومولاها", transliteration: "Allahumma ati nafsi taqwaha, wa zakkiha Anta khayru man zakkaha, Anta waliyyuha wa mawlaha.", translation: "O Allah, give my soul its piety and purify it, for You are the best to purify it. You are its Guardian and its Master.", source: "(Muslim: 2722)" },
    { id: 225, category: "prophetic", arabic: "اللهم إني أعوذ بك مِن علمٍ لا ينفع، ومن قلبٍ لا يخشع، ومن نفسٍ لا تشبع، ومن دعوةٍ لا يستجاب لها", transliteration: "Allahumma inni a'udhu bika min 'ilmin la yanfa', wa min qalbin la yakhsha', wa min nafsin la tashba', wa min da'watin la yustajabu laha.", translation: "O Allah, I seek refuge in You from knowledge that does not benefit, from a heart that does not fear (You), from a soul that is not satisfied, and from a supplication that is not answered.", source: "(Muslim: 2722)" },
    { id: 226, category: "prophetic", arabic: "اللهم اغفِرْ لي ما قدمتُ وما أخرت، وما أسررت وما أعلنت، وما أنت أعلم به مني، أنت المقدِّم وأنت المؤخِّر، وأنت على كل شيءٍ قدير", transliteration: "Allahummaghfir li ma qaddamtu wa ma akhkhart, wa ma asrartu wa ma a'lant, wa ma Anta a'lamu bihi minni. Antal-Muqaddimu wa Antal-Mu'akhkhir, wa Anta 'ala kulli shay'in qadir.", translation: "O Allah, forgive me what I have sent before me and what I have left behind, what I have concealed and what I have declared, and what You are more knowledgeable about than me. You are the Advancer and You are the Delayer, and You are over all things competent.", source: "(Muslim: 2719)" },
    { id: 227, category: "prophetic", arabic: "اللهم إني أعوذ بك مِن زوال نعمتك، وتحوُّل عافيتك، وفُجاءة نقمتك، وجميع سخَطك", transliteration: "Allahumma inni a'udhu bika min zawali ni'matik, wa tahawwuli 'afiyatik, wa fuja'ati niqmatik, wa jami'i sakhatik.", translation: "O Allah, I seek refuge in You from the withholding of Your favor, the decline of Your preservation, the suddenness of Your punishment, and all of Your displeasure.", source: "(Muslim: 2739)" },
    { id: 228, category: "prophetic", arabic: "اللهم أعوذ برضاك مِن سخَطك، وبمعافاتك من عقوبتك، وأعوذ بك منك، لا أحصي ثناءً عليك، أنتَ كما أثنيت على نفسك", transliteration: "Allahumma a'udhu bi-ridaka min sakhatik, wa bi-mu'afatika min 'uqubatik, wa a'udhu bika mink, la uhsi thana'an 'alayk, Anta kama athnayta 'ala nafsik.", translation: "O Allah, I seek refuge in Your pleasure from Your anger, and in Your forgiveness from Your punishment, and I seek refuge in You from You. I cannot enumerate Your praise. You are as You have praised Yourself.", source: "(Muslim: 486)" },
    { id: 229, category: "prophetic", arabic: "ربِّ، قِني عذابَك، يوم تبعثُ عبادَك", transliteration: "Rabbi qini 'adhabaka yawma tab'athu 'ibadak.", translation: "My Lord, save me from Your punishment on the Day You resurrect Your slaves.", source: "(Muslim: 709)" },
    { id: 230, category: "prophetic", arabic: "أعوذ بكلمات الله التامات من شر ما خلَق", transliteration: "A'udhu bikalimatillahit-tammati min sharri ma khalaq.", translation: "I seek refuge in the Perfect Words of Allah from the evil of what He has created.", source: "(Muslim: 2708)" },
    { id: 231, category: "prophetic", arabic: "أعوذ بالله من الفتن؛ ما ظهَر منها وما بطَن", transliteration: "A'udhu billahi minal-fitan, ma zahara minha wa ma batan.", translation: "I seek refuge in Allah from trials, the apparent of them and the hidden.", source: "(Muslim: 2867)" },
    { id: 232, category: "prophetic", arabic: "اللهم ربَّ جبرائيل وميكائيل وإسرافيل، فاطر السموات والأرض، عالم الغيب والشهادة، أنت تحكم بين عبادك فيما كانوا فيه يختلفون، اهدِني لما اختُلف فيه من الحق بإذنك؛ إنك تهدي مَن تشاء إلى صراطٍ مستقيمٍ", transliteration: "Allahumma Rabba Jibra'ila wa Mika'ila wa Israfil, Fatiras-samawati wal-ard, 'Alimal-ghaybi wash-shahadah, Anta tahkumu bayna 'ibadika fima kanu fihi yakhtalifun. Ihdini limakhtulifa fihi minal-haqqi bi-idhnik, innaka tahdi man tasha'u ila siratim-mustaqim.", translation: "O Allah, Lord of Gabriel, Michael, and Israfil, Creator of the heavens and the earth, Knower of the unseen and the witnessed. You judge between Your slaves concerning that wherein they differ. Guide me to the truth regarding that wherein there is difference, by Your permission. Indeed, You guide whom You will to a straight path.", source: "(Muslim: 770)" },
    { id: 233, category: "prophetic", arabic: "اللهم أنت الملك، لا إله إلا أنت، أنت ربي، وأنا عبدك، ظلمتُ نفسي، واعترفت بذنبي، فاغفر لي ذنوبي جميعًا، إنه لا يغفر الذنوب إلا أنت، واهدِني لأحسن الأخلاق، لا يهدي لأحسنها إلا أنت، واصرِف عني سيِّئَها، لا يصرف عني سيئها إلا أنت، لبيك وسعديك، والخير كلُّه في يديك، والشرُّ ليس إليك، أنا بكَ وإليك، تباركتَ وتعاليت، أستغفرك وأتوب إليك", transliteration: "Allahumma Antal-Malik, la ilaha illa Ant. Anta Rabbi wa ana 'abduk. Zalamtu nafsi wa'taraftu bidhanbi faghfir li dhunubi jami'a, innahu la yaghfirudh-dhunuba illa Ant. Wahdini li-ahsanil-akhlaq, la yahdi li-ahsaniha illa Ant. Wasrif 'anni sayyi'aha, la yasrifu 'anni sayyi'aha illa Ant. Labbayka wa sa'dayk, wal-khayru kulluhu fi yadayk, wash-sharru laysa ilayk. Ana bika wa ilayk. Tabarakta wa ta'alayt. Astaghfiruka wa atubu ilayk.", translation: "O Allah, You are the King, there is no god but You. You are my Lord and I am Your slave. I have wronged myself and I confess my sin, so forgive me all my sins, for no one can forgive sins except You. Guide me to the best of manners, for no one guides to the best of them except You. And turn away from me the bad manners, for no one turns away their badness except You. Here I am and blessed to be with You. All good is in Your Hands, and evil is not attributed to You. I am with You and unto You. Blessed and Exalted are You. I seek Your forgiveness and repent to You.", source: "(Muslim: 771)" },
    { id: 234, category: "prophetic", arabic: "اللهم ربَّ السموات ورب الأرض ورب العرش العظيم، ربنا ورب كل شيءٍ، فالقَ الحب والنوى، ومنزلَ التوراة والإنجيل والفرقان، أعوذ بك من شر كل شيءٍ أنت آخذٌ بناصيته", transliteration: "Allahumma Rabbas-samawati wa Rabbal-ard wa Rabbal-'Arshil-'Azim, Rabbana wa Rabba kulli shay', Faliqal-habbi wan-nawa, wa Munzilat-Tawrati wal-Injili wal-Furqan, a'udhu bika min sharri kulli shay'in Anta akhidhun bi-nasiyatih.", translation: "O Allah, Lord of the heavens and Lord of the earth and Lord of the Magnificent Throne, our Lord and the Lord of everything, Splitter of the grain and the date-stone, Revealer of the Torah, the Gospel, and the Criterion (the Qur'an), I seek refuge in You from the evil of every thing You seize by the forelock.", source: "(Muslim: 2713)" },
    { id: 235, category: "prophetic", arabic: "اللهم أنت الأولُ فليس قبلك شيء، وأنت الآخِر فليس بعدك شيء، وأنت الظاهر فليس فوقك شيء، وأنت الباطن فليس دونك شيء؛ اقضِ عنا الدَّين، وأغنِنا من الفقر", transliteration: "Allahumma Antal-Awwalu falaysa qablaka shay', wa Antal-Akhiru falaysa ba'daka shay', wa Antaz-Zahiru falaysa fawqaka shay', wa Antal-Batinu falaysa dunaka shay', iqdi 'annad-dayna wa aghnina minal-faqr.", translation: "O Allah, You are the First and there is nothing before You. You are the Last and there is nothing after You. You are the Manifest and there is nothing above You. You are the Hidden and there is nothing beyond You. Settle our debt and enrich us from poverty.", source: "(Muslim: 2713)" },
    { id: 236, category: "prophetic", arabic: "اللهم لك أسلمتُ، وبك آمنت، وعليك توكلت، وإليك أنَبْتُ، وبك خاصمت، اللهم إني أعوذ بعزتك، لا إله إلا أنت، أن تضلَّني، أنت الحي الذي لا يموت، والجنُّ والإنس يموتون", transliteration: "Allahumma laka aslamtu, wa bika amantu, wa 'alayka tawakkaltu, wa ilayka anabtu, wa bika khasamtu. Allahumma inni a'udhu bi-'izzatika la ilaha illa Anta an tudillani. Antal-Hayyul-ladhi la yamutu wal-jinnu wal-insu yamutun.", translation: "O Allah, to You I have submitted, and in You I have believed, and upon You I have relied, and to You I have returned, and with Your help I have argued. O Allah, I seek refuge in Your Glory - there is no god but You - lest You lead me astray. You are the Ever-Living who does not die, while jinn and mankind die.", source: "(Muslim: 2717)" },
    { id: 237, category: "prophetic", arabic: "سبحان الله وبحمده، أستغفر اللهَ وأتوب إليه", transliteration: "Subhanallahi wa bi-hamdih, astaghfirullaha wa atubu ilayh.", translation: "Glory is to Allah and to Him is the praise. I seek forgiveness from Allah and repent to Him.", source: "(Muslim: 484)" },
    { id: 238, category: "prophetic", arabic: "اللهم منزِلَ الكتاب، ومجرِيَ السحاب، وهازم الأحزاب، اهزمهم، وانصرنا عليهم", transliteration: "Allahumma Munzilal-Kitab, wa Mujriyas-sahab, wa Hazimal-Ahzab, ihzimhum wansurna 'alayhim.", translation: "O Allah, Revealer of the Book, Mover of the clouds, Defeater of the clans, defeat them and grant us victory over them.", source: "(Muslim: 1742)" },
    { id: 239, category: "prophetic", arabic: "اللهم إني أسألك من فضلك", transliteration: "Allahumma inni as'aluka min fadlik.", translation: "O Allah, I ask You from Your bounty.", source: "(Sahih Abi Dawud: 440)" },
    { id: 240, category: "prophetic", arabic: "يا حيُّ، يا قيُّوم، برحمتك أستغيث، أصلِحْ لي شأني كله، ولا تكِلْني إلى نفسي طرفة عينٍ", transliteration: "Ya Hayyu ya Qayyum, bi-rahmatika astaghith, aslih li sha'ni kullah, wa la takilni ila nafsi tarfata 'ayn.", translation: "O Ever Living One, O Eternal One, by Your mercy I call on You to set right all my affairs. Do not place me in charge of my soul even for the blinking of an eye.", source: "(Sahih At-Targhib: 654)" },
    { id: 241, category: "prophetic", arabic: "اللهم عافِني في بدني، اللهم عافني في سمعي، اللهم عافني في بصري، لا إله إلا أنت، اللهم إني أعوذ بك من الكفر والفقر، اللهم إني أعوذ بك من عذاب القبر، لا إله إلا أنت", transliteration: "Allahumma 'afini fi badani, Allahumma 'afini fi sam'i, Allahumma 'afini fi basari, la ilaha illa Ant. Allahumma inni a'udhu bika minal-kufri wal-faqr, Allahumma inni a'udhu bika min 'adhabil-qabr, la ilaha illa Ant.", translation: "O Allah, make me healthy in my body. O Allah, preserve for me my hearing. O Allah, preserve for me my sight. There is no god but You. O Allah, I seek refuge in You from disbelief and poverty and I seek refuge in You from the punishment of the grave. There is no god but You.", source: "(Sahih Abi Dawud: 4245)" },
    { id: 242, category: "prophetic", arabic: "اللهم إني أسألك العافية في الدنيا والآخرة، اللهم إني أسألك العفو والعافية في دِيني ودنياي، وأهلي ومالي، اللهم استُرْ عوَرْاتي، وآمِنْ رَوْعاتي، اللهم احفظني من بين يديَّ، ومِن خلفي، وعن يميني، وعن شمالي، ومِن فوقي، وأعوذ بعظمتك أن أُغتالَ من تحتي", transliteration: "Allahumma inni as'alukal-'afiya fid-dunya wal-akhirah. Allahumma inni as'alukal-'afwa wal-'afiyata fi dini wa dunyaya wa ahli wa mali. Allahummastur 'awrati wa amin raw'ati. Allahummahfazni min bayni yadayya wa min khalfi wa 'an yamini wa 'an shimali wa min fawqi, wa a'udhu bi-'azamatika an ughtala min tahti.", translation: "O Allah, I ask You for well-being in this world and the Hereafter. O Allah, I ask You for pardon and well-being in my religion and my worldly affairs, and my family and my wealth. O Allah, veil my weaknesses and set at ease my dismay. O Allah, preserve me from the front and from behind and on my right and on my left and from above, and I seek refuge in Your Greatness lest I be swallowed up by the earth.", source: "(Sahih Abi Dawud: 4239)" },
    { id: 243, category: "prophetic", arabic: "اللهم إني أعوذ بك من الجوع؛ فإنه بئس الضجيعُ، وأعوذ بك من الخيانة؛ فإنها بئست البِطانة", transliteration: "Allahumma inni a'udhu bika minal-ju', fa-innahu bi'sad-daji'. Wa a'udhu bika minal-khiyana, fa-innaha bi'satil-bitana.", translation: "O Allah, I seek refuge in You from hunger; surely, it is a bad companion. And I seek refuge in You from treachery; surely, it is a bad inner trait.", source: "(Sahih Abi Dawud: 1368)" },
    { id: 244, category: "prophetic", arabic: "أعوذ بالله العظيم، وبوجهه الكريم، وسلطانه القديم، من الشيطان الرجيم", transliteration: "A'udhu billahil-'Azim, wa bi-Wajhihil-Karim, wa Sultanihil-Qadim, minash-Shaytanir-Rajim.", translation: "I seek refuge in Allah the Mighty, in His Noble Face, and in His Eternal Authority, from the accursed Satan.", source: "(Sahih Abi Dawud: 441)" },
    { id: 245, category: "prophetic", arabic: "اللهم إني عبدك، وابن عبدك، وابن أَمَتك، ناصيتي بيدك، ماضٍ فيَّ حكمُك، عدل فيَّ قضاؤك، أسألك بكل اسمٍ هو لك، سميتَ به نفسك، أو علمته أحدًا من خَلْقك، أو أنزلته في كتابك، أو استأثرت به في علم الغيب عندك، أن تجعلَ القرآن ربيع قلبي، ونور صدري، وجِلاء حُزني، وذهاب همي", transliteration: "Allahumma inni 'abduk, wabnu 'abdik, wabnu amatik, nasiyati bi-yadik, madin fiyya hukmuk, 'adlun fiyya qada'uk. As'aluka bi-kulli ismin huwa lak, sammayta bihi nafsak, aw 'allamtahu ahadan min khalqik, aw anzaltahu fi kitabik, awista'tharta bihi fi 'ilmil-ghaybi 'indak, an taj'alal-Qur'ana rabi'a qalbi, wa nura sadri, wa jila'a huzni, wa dhahaba hammi.", translation: "O Allah, I am Your slave, son of Your slave, son of Your female slave, my forelock is in Your Hand, Your command over me is forever executed and Your decree over me is just. I ask You by every Name belonging to You which You named Yourself with, or revealed in Your Book, or You taught to any of Your creation, or You have preserved in the knowledge of the unseen with You, that You make the Qur'an the spring of my heart and the light of my breast, and a departure for my sorrow and a release for my anxiety.", source: "(Sahih Al-Kalim At-Tayyib: 49)" },
    { id: 246, category: "prophetic", arabic: "اللهم رحمتَك أرجو؛ فلا تكِلْني إلى نفسي طرفة عينٍ، وأصلِحْ لي شأني كله، لا إله إلا أنت", transliteration: "Allahumma rahmataka arju, fala takilni ila nafsi tarfata 'ayn, wa aslih li sha'ni kullah, la ilaha illa Ant.", translation: "O Allah, I hope for Your mercy. Do not leave me to myself even for the blinking of an eye. Correct all of my affairs for me. There is no god but You.", source: "(Sahih Abi Dawud: 4246)" },
    { id: 247, category: "prophetic", arabic: "اللهم إني أسألك الجنة، وأعوذ بك مِن النار", transliteration: "Allahumma inni as'alukal-Jannah, wa a'udhu bika minan-nar.", translation: "O Allah, I ask You for Paradise and I seek refuge in You from the Fire.", source: "(Sahih Abi Dawud: 710)" },
    { id: 248, category: "prophetic", arabic: "اللهم إني أسألك يا أللهُ، الأحد الصمد، الذي لم يلِدْ ولم يولد، ولم يكن له كُفُوًا أحد، أن تغفرَ لي ذنوبي؛ إنك أنت الغفور الرحيم", transliteration: "Allahumma inni as'aluka ya Allah, Al-Ahadus-Samad, alladhi lam yalid wa lam yulad, wa lam yakun lahu kufuwan ahad, an taghfira li dhunubi, innaka Antal-Ghafurur-Rahim.", translation: "O Allah, I ask You, O Allah, the One, the Eternal Refuge, who neither begets nor is born, and there is no equivalent to Him, that You forgive me my sins. Indeed, You are the Forgiving, the Merciful.", source: "(Sahih Abi Dawud: 869)" },
    { id: 249, category: "prophetic", arabic: "اللهم اغفِرْ لي ما قدمتُ وما أخرت، وما أسررت وما أعلنت، وما أسرفتُ وما أنت أعلم به مني، أنت المقدِّم وأنت المؤخِّر، لا إله إلا أنت", transliteration: "Allahummaghfir li ma qaddamtu wa ma akhkhart, wa ma asrartu wa ma a'lant, wa ma asraftu wa ma Anta a'lamu bihi minni. Antal-Muqaddimu wa Antal-Mu'akhkhir, la ilaha illa Ant.", translation: "O Allah, forgive me what I have sent before me and what I have left behind, what I have concealed and what I have declared, what I have exceeded in and what You are more knowledgeable about than me. You are the Advancer and You are the Delayer, there is no god but You.", source: "(Sahih Abi Dawud: 1336)" },
    { id: 250, category: "prophetic", arabic: "اللهم أعنِّي على ذِكرك، وشُكرك، وحُسن عبادتك", transliteration: "Allahumma a'inni 'ala dhikrika, wa shukrika, wa husni 'ibadatik.", translation: "O Allah, help me to remember You, to give You thanks, and to perform Your worship in the best manner.", source: "(Sahih Abi Dawud: 1347)" },
    { id: 251, category: "prophetic", arabic: "اللهم إني أعوذ بك مِن علمٍ لا ينفع، ومن قلبٍ لا يخشع، ومن نفسٍ لا تشبع، ومن دعاءٍ لا يُسمَع", transliteration: "Allahumma inni a'udhu bika min 'ilmin la yanfa', wa min qalbin la yakhsha', wa min nafsin la tashba', wa min du'a'in la yusma'.", translation: "O Allah, I seek refuge in You from knowledge that does not benefit, from a heart that does not fear (You), from a soul that is not satisfied, and from a supplication that is not heard.", source: "(Sahih Abi Dawud: 1369)" },
    { id: 252, category: "prophetic", arabic: "اللهم إني أعوذ بك من صلاةٍ لا تنفع", transliteration: "Allahumma inni a'udhu bika min salatin la tanfa'.", translation: "O Allah, I seek refuge in You from a prayer that does not benefit.", source: "(Sahih Abi Dawud: 1370)" },
    { id: 253, category: "prophetic", arabic: "اللهم إني أعوذ بك مِن شر سمعي، ومِن شر بصري، ومن شر لساني، ومن شر قلبي، ومن شر مَنيِّي 'أي ذكَري'", transliteration: "Allahumma inni a'udhu bika min sharri sam'i, wa min sharri basari, wa min sharri lisani, wa min sharri qalbi, wa min sharri maniyyi.", translation: "O Allah, I seek refuge in You from the evil of my hearing, and from the evil of my sight, and from the evil of my tongue, and from the evil of my heart, and from the evil of my semen (i.e., sexual passion).", source: "(Sahih Abi Dawud: 1372)" },
    { id: 254, category: "prophetic", arabic: "اللهم إني أعوذ بك مِن الهَدْم، وأعوذ بك مِن التردِّي، وأعوذ بك مِن الغرق، والحرق، والهَرَم، وأعوذ بك أن يتخبطني الشيطان عند الموت، وأعوذ بك أن أموتَ في سبيلك مدبِرًا، وأعوذ بك أن أموتَ لديغًا", transliteration: "Allahumma inni a'udhu bika minal-hadm, wa a'udhu bika minat-taraddi, wa a'udhu bika minal-gharaq, wal-haraq, wal-haram, wa a'udhu bika an yatakhabbatanish-shaytanu 'indal-mawt, wa a'udhu bika an amuta fi sabilika mudbira, wa a'udhu bika an amuta ladigha.", translation: "O Allah, I seek refuge in You from being buried alive (by debris), and I seek refuge in You from falling down (from a height), and I seek refuge in You from drowning, burning, and senility. And I seek refuge in You from the Devil confusing me at the time of death, and I seek refuge in You from dying in Your cause while fleeing (from battle), and I seek refuge in You from dying from a sting (of a venomous creature).", source: "(Sahih Abi Dawud: 1373)" },
    { id: 255, category: "prophetic", arabic: "ربِّ، أعنِّي ولا تُعِنْ عليَّ، وانصرني ولا تنصر عليَّ، وامكر لي ولا تمكُرْ عليَّ، واهدِني، ويسِّر لي الهدى، وانصرني على مَن بغى علي", transliteration: "Rabbi a'inni wa la tu'in 'alayya, wansurni wa la tansur 'alayya, wamkur li wa la tamkur 'alayya, wahdini wa yassir lil-huda, wansurni 'ala man bagha 'alayya.", translation: "My Lord, aid me and do not aid against me; grant me victory and do not grant victory against me; plan for me and do not plan against me; guide me and facilitate guidance for me; and grant me victory over those who transgress against me.", source: "(Sahih Abi Dawud: 1337)" },
    { id: 256, category: "prophetic", arabic: "ربِّ، اجعَلْني لك شكَّارًا، لك ذكَّارًا، لك رهَّابًا، لك مِطواعًا، لك مُخبِتًا، إليك أوَّاهًا منيبًا", transliteration: "Rabbij'alni laka shakkar, laka dhakkar, laka rahhab, laka mitwa'a, laka mukhbita, ilayka awwaham-muniba.", translation: "My Lord, make me ever grateful to You, ever remembering of You, ever fearful of You, ever obedient to You, ever humble to You, ever turning to You and repenting.", source: "(Sahih Abi Dawud: 1337)" },
    { id: 257, category: "prophetic", arabic: "ربِّ، تقبَّل توبتي، واغسل حَوْبتي، وأجِبْ دعوتي، وثبِّتْ حجتي، وسدِّد لساني، واهدِ قلبي، واسلُلْ سَخِيمة 'حِقد' صدري", transliteration: "Rabbi taqabbal tawbati, waghsil hawbati, wa ajib da'wati, wa thabbit hujjati, wa saddid lisani, wahdi qalbi, waslul sakhimata sadri.", translation: "My Lord, accept my repentance, wash away my sin, answer my supplication, establish my proof, guide my heart, make my tongue truthful, and remove the malice of my breast.", source: "(Sahih Abi Dawud: 1337)" },
    { id: 258, category: "prophetic", arabic: "اللهم إني أعوذ بك من البَرَص، والجنون، والجُذَام، ومن سيِّئ الأسقام", transliteration: "Allahumma inni a'udhu bika minal-baras, wal-junun, wal-judham, wa min sayyi'il-asqam.", translation: "O Allah, I seek refuge in You from leprosy, insanity, mutilation, and from all serious illnesses.", source: "(Sahih Abi Dawud: 1375)" },
    { id: 259, category: "prophetic", arabic: "ربِّ، اغفِرْ لي، وتب عليَّ؛ إنك أنت التواب الرحيم", transliteration: "Rabbighfir li, wa tub 'alayya, innaka Antat-Tawwabur-Rahim.", translation: "My Lord, forgive me and accept my repentance; indeed, You are the Accepting of repentance, the Merciful.", source: "(Sahih Abi Dawud: 1342)" },
    { id: 260, category: "prophetic", arabic: "أعوذ بالله السميع العليم مِن الشيطان الرجيم؛ مِن هَمْزه 'الجنون'، ونَفْخه 'الكِبْر'، ونَفْثه 'الشِّعر'", transliteration: "A'udhu billahis-Sami'il-'Alim minash-Shaytanir-Rajim, min hamzihi wa nafkhihi wa nafthih.", translation: "I seek refuge in Allah, the All-Hearing, the All-Knowing, from the accursed Satan, from his spurring to madness, his blowing of pride, and his spitting of poetry.", source: "(Sahih Abi Dawud: 701)" },
    { id: 261, category: "prophetic", arabic: "الحمد لله رب العالمين، الرحمن الرحيم، ملك يوم الدين، لا إله إلا الله، يفعل ما يريد، اللهم أنت الله، لا إله إلا أنت، الغنيُّ ونحن الفقراء، أنزِلْ علينا الغيثَ، واجعل ما أنزلتَ لنا قوةً وبلاغًا إلى حينٍ", transliteration: "Al-hamdu lillahi Rabbil-'Alamin, Ar-Rahmanir-Rahim, Maliki Yawmid-Din, la ilaha illallah, yaf'alu ma yurid. Allahumma Antallah, la ilaha illa Ant, Al-Ghaniyyu wa nahnul-fuqara', anzil 'alaynal-ghayth, waj'al ma anzalta lana quwwatan wa balaghan ila hin.", translation: "All praise is due to Allah, Lord of the worlds, the Entirely Merciful, the Especially Merciful, Sovereign of the Day of Recompense. There is no god but Allah; He does what He wills. O Allah, You are Allah, there is no god but You. You are the Rich (Free of need) and we are the poor. Send down rain upon us and make what You send down for us strength and satisfaction for a time.", source: "(Sahih Abi Dawud: 1173)" },
    { id: 262, category: "prophetic", arabic: "اللهم إني أعوذ بك مِن فتنة النار، وعذاب النار، ومِن شر الغنى والفقر", transliteration: "Allahumma inni a'udhu bika min fitnatin-nar, wa 'adhabin-nar, wa min sharril-ghina wal-faqr.", translation: "O Allah, I seek refuge in You from the trial of the Fire, and the punishment of the Fire, and from the evil of wealth and poverty.", source: "(Sahih Abi Dawud: 1365)" },
    { id: 263, category: "prophetic", arabic: "أستغفِر اللهَ الذي لا إله إلا هو الحيَّ القيُّومَ، وأتوب إليه", transliteration: "Astaghfirullahal-ladhi la ilaha illa Huwal-Hayyul-Qayyum, wa atubu ilayh.", translation: "I seek forgiveness from Allah, whom there is no god but He, the Ever-Living, the Eternal, and I repent to Him.", source: "(Sahih Abi Dawud: 1343)" },
    { id: 264, category: "prophetic", arabic: "اللهم إنا نجعلك في نحورِهم، ونعوذ بك مِن شرورهم", transliteration: "Allahumma inna naj'aluka fi nuhurihim, wa na'udhu bika min shururihim.", translation: "O Allah, we place You before them, and we seek refuge in You from their evils.", source: "(Sahih Abi Dawud: 1360)" },
    { id: 265, category: "prophetic", arabic: "اللهم إني أعوذ بك من الفقر، والقلَّة، والذلَّة، وأعوذ بك مِن أن أُظلَم، أو أَظلِم", transliteration: "Allahumma inni a'udhu bika minal-faqr, wal-qillah, wadh-dhillah, wa a'udhu bika min an uzlama aw azlim.", translation: "O Allah, I seek refuge in You from poverty, scarcity, and humiliation, and I seek refuge in You from being wronged or wronging others.", source: "(Sahih Abi Dawud: 1366)" },
    { id: 266, category: "prophetic", arabic: "اللهم أعوذ بك أن أضِلَّ أو أضَلَّ، أو أزِلَّ أو أزَلَّ، أو أَظلِم أو أُظلَم، أو أجهَل أو يُجهَل عليَّ", transliteration: "Allahumma a'udhu bika an adilla aw udall, aw azilla aw uzall, aw azlima aw uzlam, aw ajhala aw yujhala 'alayy.", translation: "O Allah, I seek refuge in You from going astray or being led astray, from slipping or being made to slip, from oppressing or being oppressed, from behaving ignorantly or being treated ignorantly.", source: "(Sahih Abi Dawud: 4248)" },
    { id: 267, category: "prophetic", arabic: "يا مقلِّب القلوب، ثبِّت قلبي على دِينك", transliteration: "Ya Muqallibal-qulub, thabbit qalbi 'ala dinik.", translation: "O Turner of the hearts, make my heart steadfast on Your religion.", source: "(Sahih At-Tirmidhi: 2792)" },
    { id: 268, category: "prophetic", arabic: "اللهم اهدِني فيمن هدَيتَ، وعافني فيمن عافيتَ، وتولَّني فيمن تولَّيتَ، وبارك لي فيما أعطيتَ، وقِني شر ما قضيت؛ فإنك تقضي ولا يقضى عليك، وإنه لا يَذِلُّ مَن واليتَ، تباركتَ ربَّنا وتعاليت", transliteration: "Allahummahdini fiman hadayt, wa 'afini fiman 'afayt, wa tawallani fiman tawallayt, wa barik li fima a'tayt, wa qini sharra ma qadayt, fa-innaka taqdi wa la yuqda 'alayk, wa innahu la yadhillu man walayt, tabarakta Rabbana wa ta'alayt.", translation: "O Allah, guide me among those You have guided, heal me among those You have healed, befriend me among those You have befriended, bless me in what You have granted, and save me from the evil of what You have decreed. Indeed, You decree, and none can decree against You. Indeed, he is not humiliated whom You have befriended. Blessed are You, our Lord, and Exalted.", source: "(Sahih At-Tirmidhi: 411)" },
    { id: 269, category: "prophetic", arabic: "اللهم إني أسألك فِعل الخيرات، وترك المنكَرات، وحبَّ المساكين، وإذا أردتَ بعبادك فتنةً، فاقبِضْني إليك غيرَ مفتونٍ", transliteration: "Allahumma inni as'aluka fi'lal-khayrat, wa tarkal-munkarat, wa hubbal-masakin, wa idha aradta bi-'ibadika fitnatan, faqbidni ilayka ghayra maftun.", translation: "O Allah, I ask You (to enable me) to do good deeds, to avoid evil deeds, and to love the poor. And if You intend a trial for Your servants, then take me to You without being subjected to the trial.", source: "(Sahih At-Tirmidhi: 2580)" },
    { id: 270, category: "prophetic", arabic: "اللهم اقسِمْ لنا مِن خشيتك ما يحول بيننا وبين معاصيك، ومِن طاعتك ما تبلِّغنا به جنتَك، ومِن اليقين ما تهوِّن به علينا مصيبات الدنيا، ومتِّعنا بأسماعنا وأبصارنا وقوَّتنا ما أحييتَنا، واجعله الوارثَ منا، واجعل ثأرَنا على مَن ظلمنا، وانصرنا على مَن عادانا، ولا تجعل مصيبتَنا في دِيننا، ولا تجعل الدنيا أكبرَ همنا، ولا مبلَغ علمنا، ولا تسلِّط علينا مَن لا يرحمنا", transliteration: "Allahummaqsim lana min khashyatika ma yahulu baynana wa bayna ma'asik, wa min ta'atika ma tuballighuna bihi jannatak, wa minal-yaqini ma tuhawwinu bihi 'alayna musibatid-dunya, wa matti'na bi-asma'ina wa absarina wa quwwatina ma ahyaytana, waj'alhul-waritha minna, waj'al tha'rana 'ala man zalamana, wansurna 'ala man 'adana, wa la taj'al musibatana fi dinina, wa la taj'alid-dunya akbara hammina, wa la mablagha 'ilmina, wa la tusallit 'alayna man la yarhamuna.", translation: "O Allah, apportion for us a fear of You that will prevent us from disobeying You, and obedience to You that will make us reach Your Paradise, and certainty that will make the calamities of this world easy for us. And let us enjoy our hearing, our sight, and our power as long as You keep us alive, and make it the heir of us (remain until death). And place our vengeance upon those who have wronged us, and support us against those who are hostile to us, and do not make our calamity in our religion, and do not make the world our greatest concern, nor the limit of our knowledge, and do not impose upon us those who do not show mercy to us.", source: "(Sahih At-Tirmidhi: 2783)" },
    { id: 271, category: "prophetic", arabic: "اللهم إني أعوذ بك من منكَرات الأخلاق، والأعمال، والأهواء", transliteration: "Allahumma inni a'udhu bika min munkaratil-akhlaq, wal-a'mal, wal-ahwa'.", translation: "O Allah, I seek refuge in You from repugnant morals, deeds, and desires.", source: "(Sahih At-Tirmidhi: 2840)" },
    { id: 272, category: "prophetic", arabic: "اللهم انفَعْني بما علَّمتَني، وعلِّمني ما ينفعني، وزِدْني علمًا", transliteration: "Allahummanfa'ni bima 'allamtani, wa 'allimni ma yanfa'uni, wa zidni 'ilma.", translation: "O Allah, benefit me with what You have taught me, and teach me what will benefit me, and increase me in knowledge.", source: "(Sahih At-Tirmidhi: 2845)" },
    { id: 273, category: "prophetic", arabic: "اللهم فاطرَ السموات والأرض، عالم الغيب والشهادة، لا إله إلا أنت، ربَّ كل شيءٍ ومليكَه، أعوذ بك مِن شر نفسي، ومن شر الشيطان وشِركِه، وأن أقترف على نفسي سوءًا، أو أجُرَّه إلى مسلمٍ", transliteration: "Allahumma Fatiras-samawati wal-ard, 'Alimal-ghaybi wash-shahadah, la ilaha illa Ant, Rabba kulli shay'in wa Malikah. A'udhu bika min sharri nafsi, wa min sharrish-shaytani wa shirkih, wa an aqtarifa 'ala nafsi su'an, aw ajurrahu ila muslim.", translation: "O Allah, Creator of the heavens and the earth, Knower of the unseen and the witnessed, there is no god but You, Lord of all things and their Sovereign. I seek refuge in You from the evil of my soul and from the evil of Satan and his shirk (associating partners with Allah), and from committing wrong against my soul or bringing such upon another Muslim.", source: "(Sahih At-Tirmidhi: 2798)" },
    { id: 274, category: "prophetic", arabic: "اللهم إني أسألك عيشةً نقيةً، وميتةً سويةً، ومردًّا غير مخزٍ ولا فاضح", transliteration: "Allahumma inni as'aluka 'ishatan naqiyyah, wa maytatan sawiyyah, wa maraddan ghayra mukhzin wa la fadih.", translation: "O Allah, I ask You for a pure life, a sound death, and a return that is neither disgraceful nor humiliating.", source: "(Ad-Du'a' by At-Tabarani, authenticated by Al-Albani)" },
    { id: 275, category: "prophetic", arabic: "اللهم متِّعني بسمعي وبصري، واجعلهما الوارثَ مني، وانصرني على مَن يظلمني، وخذ منه بثأري", transliteration: "Allahumma matti'ni bi-sam'i wa basari, waj'alhumal-waritha minni, wansurni 'ala man yazlimuni, wa khudh minhu bi-tha'ri.", translation: "O Allah, let me enjoy my hearing and my sight, and make them the heir of me (remain until death), and grant me victory over those who oppress me, and take my vengeance from them.", source: "(Sahih Al-Hakim)" },
    { id: 276, category: "prophetic", arabic: "اللهم إني أسألك العافية في الدنيا والآخرة", transliteration: "Allahumma inni as'alukal-'afiya fid-dunya wal-akhirah.", translation: "O Allah, I ask You for well-being in this world and the Hereafter.", source: "(Sahih At-Tirmidhi: 3514)" },
    { id: 277, category: "prophetic", arabic: "اللهم ربَّنا آتِنا في الدنيا حسنةً، وفي الآخرة حسنةً، وقِنا عذاب النار", transliteration: "Allahumma Rabbana atina fid-dunya hasanatan wa fil-akhirati hasanatan wa qina 'adhaban-nar.", translation: "O Allah, our Lord, give us in this world [that which is] good and in the Hereafter [that which is] good and protect us from the punishment of the Fire.", source: "(Al-Bukhari: 4522)" },
    { id: 278, category: "prophetic", arabic: "اللهم إني أسألك الهدى، والتقى، والعفاف، والغنى", transliteration: "Allahumma inni as'alukal-huda, wat-tuqa, wal-'afafa, wal-ghina.", translation: "O Allah, I ask You for guidance, piety, chastity, and self-sufficiency.", source: "(Muslim: 2721)" },
    { id: 279, category: "prophetic", arabic: "اللهم اغفِرْ لي، وارحمني، واهدِني، وعافِني، وارزقني", transliteration: "Allahummaghfir li, warhamni, wahdini, wa 'afini, warzuqni.", translation: "O Allah, forgive me, have mercy on me, guide me, heal me, and provide for me.", source: "(Muslim: 2697)" },
    { id: 280, category: "prophetic", arabic: "يا مقلِّب القلوب، ثبِّت قلبي على دِينك", transliteration: "Ya Muqallibal-qulub, thabbit qalbi 'ala dinik.", translation: "O Turner of the hearts, make my heart steadfast on Your religion.", source: "(Sahih At-Tirmidhi: 2140)" },
    { id: 281, category: "prophetic", arabic: "اللهم إني أعوذ بك من العجز، والكسل، والجبن، والبخل، والهرم، وعذاب القبر، اللهم آتِ نفسي تقواها، وزكِّها أنت خيرُ مَن زكاها، أنت وليُّها ومولاها، اللهم إني أعوذ بك من علمٍ لا ينفع، ومن قلبٍ لا يخشع، ومن نفسٍ لا تشبع، ومن دعوةٍ لا يستجاب لها", transliteration: "Allahumma inni a'udhu bika minal-'ajzi, wal-kasali, wal-jubni, wal-bukhli, wal-harami, wa 'adhabil-qabr. Allahumma ati nafsi taqwaha, wa zakkiha Anta khayru man zakkaha, Anta waliyyuha wa mawlaha. Allahumma inni a'udhu bika min 'ilmin la yanfa', wa min qalbin la yakhsha', wa min nafsin la tashba', wa min da'watin la yustajabu laha.", translation: "O Allah, I seek refuge in You from incapacity, laziness, cowardice, miserliness, senility, and the punishment of the grave. O Allah, give my soul its piety and purify it, for You are the best to purify it. You are its Guardian and its Master. O Allah, I seek refuge in You from knowledge that does not benefit, from a heart that does not fear (You), from a soul that is not satisfied, and from a supplication that is not answered.", source: "(Muslim: 2722)" },
    { id: 282, category: "prophetic", arabic: "اللهم إني أعوذ بك من شر ما عملتُ، ومن شر ما لم أعمل", transliteration: "Allahumma inni a'udhu bika min sharri ma 'amiltu, wa min sharri ma lam a'mal.", translation: "O Allah, I seek refuge in You from the evil of what I have done, and from the evil of what I have not done.", source: "(Muslim: 2716)" },
    { id: 283, category: "prophetic", arabic: "اللهم إني أعوذ بك من زوال نعمتك، وتحوُّل عافيتك، وفُجاءة نقمتك، وجميع سخَطك", transliteration: "Allahumma inni a'udhu bika min zawali ni'matik, wa tahawwuli 'afiyatik, wa fuja'ati niqmatik, wa jami'i sakhatik.", translation: "O Allah, I seek refuge in You from the withholding of Your favor, the decline of Your preservation, the suddenness of Your punishment, and all of Your displeasure.", source: "(Muslim: 2739)" },
    { id: 284, category: "prophetic", arabic: "اللهم إني أعوذ بك من شر سمعي، ومن شر بصري، ومن شر لساني، ومن شر قلبي، ومن شر مَنيِّي", transliteration: "Allahumma inni a'udhu bika min sharri sam'i, wa min sharri basari, wa min sharri lisani, wa min sharri qalbi, wa min sharri maniyyi.", translation: "O Allah, I seek refuge in You from the evil of my hearing, and from the evil of my sight, and from the evil of my tongue, and from the evil of my heart, and from the evil of my semen (i.e., sexual passion).", source: "(Sahih Abi Dawud: 1551)" },
    { id: 285, category: "prophetic", arabic: "اللهم إني أعوذ بك من البرص، والجنون، والجذام، ومن سيئ الأسقام", transliteration: "Allahumma inni a'udhu bika minal-baras, wal-junun, wal-judham, wa min sayyi'il-asqam.", translation: "O Allah, I seek refuge in You from leprosy, insanity, mutilation, and from all serious illnesses.", source: "(Sahih Abi Dawud: 1554)" },
    { id: 286, category: "prophetic", arabic: "اللهم إني أعوذ بك من منكرات الأخلاق، والأعمال، والأهواء", transliteration: "Allahumma inni a'udhu bika min munkaratil-akhlaq, wal-a'mal, wal-ahwa'.", translation: "O Allah, I seek refuge in You from repugnant morals, deeds, and desires.", source: "(Sahih At-Tirmidhi: 3591)" },
    { id: 287, category: "prophetic", arabic: "اللهم إنك عفو كريم تحب العفو فاعف عني", transliteration: "Allahumma innaka 'Afuwwun Karimun tuhibbul-'afwa fa'fu 'anni.", translation: "O Allah, You are Forgiving and Generous and You love forgiveness, so forgive me.", source: "(Sahih At-Tirmidhi: 3513)" },
    { id: 288, category: "prophetic", arabic: "اللهم ارزقني حبك، وحب من ينفعني حبه عندك، اللهم ما رزقتني مما أحب فاجعله قوة لي فيما تحب، اللهم وما زويت عني مما أحب فاجعله فراغاً لي فيما تحب", transliteration: "Allahummarzuqni hubbak, wa hubba man yanfa'uni hubbuhu 'indak. Allahumma ma razaqtani mimma uhibbu faj'alhu quwwatan li fima tuhibb. Allahumma wa ma zawayta 'anni mimma uhibbu faj'alhu faraghan li fima tuhibb.", translation: "O Allah, grant me Your love and the love of those whose love will benefit me with You. O Allah, whatever You have provided me of that which I love, make it strength for me in that which You love. O Allah, whatever You have withheld from me of that which I love, make it free space for me in that which You love.", source: "(Sahih At-Tirmidhi: 3491)" },
    { id: 289, category: "prophetic", arabic: "اللهم طهرني من الذنوب والخطايا، اللهم نقني منها كما ينقى الثوب الأبيض من الدنس، اللهم طهرني بالثلج والبرد والماء البارد", transliteration: "Allahumma tahhirni minadh-dhunubi wal-khataya. Allahumma naqqini minha kama yunaqqath-thawbul-abyadu minad-danas. Allahumma tahhirni bith-thalji wal-baradi wal-ma'il-barid.", translation: "O Allah, purify me from sins and errors. O Allah, cleanse me from them as a white garment is cleansed from dirt. O Allah, purify me with snow, hail, and cold water.", source: "(Sahih Muslim: 476)" },
    { id: 290, category: "prophetic", arabic: "اللهم إني أعوذ بك من البخل، والجبن، وسوء العمر، وفتنة الصدر، وعذاب القبر", transliteration: "Allahumma inni a'udhu bika minal-bukhli, wal-jubni, wa su'il-'umur, wa fitnatis-sadr, wa 'adhabil-qabr.", translation: "O Allah, I seek refuge in You from miserliness, cowardice, the evils of old age, the trial of the breast, and the punishment of the grave.", source: "(Sahih An-Nasa'i: 5460)" },
    { id: 291, category: "prophetic", arabic: "اللهم رب جبرائيل وميكائيل، ورب إسرافيل، أعوذ بك من حر النار ومن عذاب القبر", transliteration: "Allahumma Rabba Jibra'ila wa Mika'ila, wa Rabba Israfil, a'udhu bika min harrin-nar wa min 'adhabil-qabr.", translation: "O Allah, Lord of Gabriel and Michael, and Lord of Israfil, I seek refuge in You from the heat of the Fire and from the punishment of the grave.", source: "(Sahih An-Nasa'i: 5521)" },
    { id: 292, category: "prophetic", arabic: "اللهم ألهمني رشدي، وأعذني من شر نفسي", transliteration: "Allahumma alhimni rushdi, wa a'idhni min sharri nafsi.", translation: "O Allah, inspire me with my guidance, and protect me from the evil of my soul.", source: "(Sahih At-Tirmidhi: 3483)" },
    { id: 293, category: "prophetic", arabic: "اللهم إني أسألك علماً نافعاً، وأعوذ بك من علم لا ينفع", transliteration: "Allahumma inni as'aluka 'ilman nafi'a, wa a'udhu bika min 'ilmin la yanfa'.", translation: "O Allah, I ask You for beneficial knowledge, and I seek refuge in You from knowledge that does not benefit.", source: "(Sahih Ibn Majah: 3114)" },
    { id: 294, category: "prophetic", arabic: "اللهم رب السماوات السبع ورب العرش العظيم، ربنا ورب كل شيء، منزل التوراة والإنجيل والفرقان، فالق الحب والنوى، أعوذ بك من شر كل شيء أنت آخذ بناصيته، أنت الأول فليس قبلك شيء، وأنت الآخر فليس بعدك شيء، وأنت الظاهر فليس فوقك شيء، وأنت الباطن فليس دونك شيء، اقض عنا الدين وأغننا من الفقر", transliteration: "Allahumma Rabbas-samawatis-sab'i wa Rabbal-'Arshil-'Azim, Rabbana wa Rabba kulli shay', Munzilat-Tawrati wal-Injili wal-Furqan, Faliqal-habbi wan-nawa, a'udhu bika min sharri kulli shay'in Anta akhidhun bi-nasiyatih. Antal-Awwalu falaysa qablaka shay', wa Antal-Akhiru falaysa ba'daka shay', wa Antaz-Zahiru falaysa fawqaka shay', wa Antal-Batinu falaysa dunaka shay', iqdi 'annad-dayna wa aghnina minal-faqr.", translation: "O Allah, Lord of the seven heavens and Lord of the Magnificent Throne, our Lord and the Lord of everything, Revealer of the Torah, the Gospel, and the Criterion (the Qur'an), Splitter of the grain and the date-stone, I seek refuge in You from the evil of every thing You seize by the forelock. You are the First and there is nothing before You. You are the Last and there is nothing after You. You are the Manifest and there is nothing above You. You are the Hidden and there is nothing beyond You. Settle our debt and enrich us from poverty.", source: "(Sahih Muslim: 2713)" },
    { id: 295, category: "prophetic", arabic: "اللهم ألف بين قلوبنا، وأصلح ذات بيننا، واهدنا سبل السلام، ونجنا من الظلمات إلى النور، وجنبنا الفواحش ما ظهر منها وما بطن، وبارك لنا في أسماعنا، وأبصارنا، وقلوبنا، وأزواجنا، وذرياتنا، وتب علينا إنك أنت التواب الرحيم، واجعلنا شاكرين لنعمك مثنين بها عليك، قابلين لها، وأتممها علينا", transliteration: "Allahumma allif bayna qulubina, wa aslih dhata baynina, wahdina subulas-salam, wa najjina minaz-zulumati ilan-nur, wa jannibnal-fawahisha ma zahara minha wa ma batan, wa barik lana fi asma'ina, wa absarina, wa qulubina, wa azwajina, wa dhurriyyatina, wa tub 'alayna innaka Antat-Tawwabur-Rahim, waj'alna shakirina li-ni'amika muthnina biha 'alayk, qabilina laha, wa atmimha 'alayna.", translation: "O Allah, join our hearts, mend our social relationship, guide us to the paths of peace, bring us out of darknesses into the light, save us from immoralities whether open or secret, and bless us in our hearing, our sight, our hearts, our wives, and our offspring. Accept our repentance; indeed, You are the Accepting of repentance, the Merciful. And make us grateful for Your blessings, praising You for them, accepting them, and complete them upon us.", source: "(Sahih Abi Dawud: 969)" },
    { id: 296, category: "prophetic", arabic: "اللهم إني أسألك خير المسألة، وخير الدعاء، وخير النجاح، وخير العمل، وخير الثواب، وخير الحياة، وخير الممات، وثبتني، وثقل موازيني، وحقق إيماني، وارفع درجاتي، وتقبل صلاتي، واغفر خطيئتي، وأسألك الدرجات العلى من الجنة", transliteration: "Allahumma inni as'aluka khayral-mas'alah, wa khayrad-du'a', wa khayran-najah, wa khayral-'amal, wa khayrath-thawab, wa khayral-hayah, wa khayral-mamat, wa thabbitni, wa thaqqil mawazini, wa haqqiq imani, warfa' darajati, wa taqabbal salati, waghfir khati'ati, wa as'alukad-darajatil-'ula minal-Jannah.", translation: "O Allah, I ask You for the best request, the best supplication, the best success, the best deed, the best reward, the best life, and the best death. Make me steadfast, weigh heavily my scales, realize my faith, raise my ranks, accept my prayer, forgive my sin, and I ask You for the high ranks of Paradise.", source: "(Sahih Al-Hakim)" },
    { id: 297, category: "prophetic", arabic: "اللهم جنبني منكرات الأخلاق، والأهواء، والأعمال، والأدواء", transliteration: "Allahumma jannibni munkaratil-akhlaq, wal-ahwa', wal-a'mal, wal-adwa'.", translation: "O Allah, keep me away from repugnant morals, desires, deeds, and diseases.", source: "(Sahih Al-Hakim)" },
    { id: 298, category: "prophetic", arabic: "اللهم قنعني بما رزقتني، وبارك لي فيه، واخلف علي كل غائبة لي بخير", transliteration: "Allahumma qanni'ni bima razaqtani, wa barik li fih, wakhluf 'ala kulli gha'ibatin li bi-khayr.", translation: "O Allah, make me content with what You have provided me, bless me in it, and replace for me every absent thing with something better.", source: "(Sahih Al-Hakim)" },
    { id: 299, category: "prophetic", arabic: "اللهم حاسبني حساباً يسيراً", transliteration: "Allahumma hasibni hisaban yasira.", translation: "O Allah, call me to a light account.", source: "(Sahih Al-Hakim)" },
    { id: 300, category: "prophetic", arabic: "اللهم أعنا على ذكرك، وشكرك، وحسن عبادتك", transliteration: "Allahumma a'inna 'ala dhikrika, wa shukrika, wa husni 'ibadatik.", translation: "O Allah, help us to remember You, to give You thanks, and to perform Your worship in the best manner.", source: "(Sahih Al-Hakim)" },
    { id: 301, category: "prophetic", arabic: "اللهم إني أسألك إيماناً لا يرتد، ونعيماً لا ينفد، ومرافقة محمد صلى الله عليه وسلم في أعلى جنة الخلد", transliteration: "Allahumma inni as'aluka imanan la yartadd, wa na'iman layanfad, wa murafaqata Muhammadin sallallahu 'alayhi wa sallama fi a'la jannatil-khuld.", translation: "O Allah, I ask You for faith that does not revert, bliss that does not deplete, and the company of Muhammad (peace and blessings of Allah be upon him) in the highest eternal Paradise.", source: "(Sahih Ibn Hibban)" },
    { id: 302, category: "prophetic", arabic: "اللهم قني شر نفسي، واعزم لي على أرشد أمري، اللهم اغفر لي ما أسررت، وما أعلنت، وما أخطأت، وما عمدت، وما علمت، وما جهلت", transliteration: "Allahumma qini sharra nafsi, wa'zim li 'ala arshadi amri. Allahummaghfir li ma asrartu, wa ma a'lantu, wa ma akhta'tu, wa ma 'amadtu, wa ma 'alimtu, wa ma jahilt.", translation: "O Allah, protect me from the evil of my soul, and resolve for me on the most right of my affairs. O Allah, forgive me what I have concealed, what I have declared, what I have done in error, what I have done intentionally, what I have known, and what I have been ignorant of.", source: "(Sahih Ibn Hibban)" },
    { id: 303, category: "prophetic", arabic: "اللهم إني أعوذ بك من غلبة الدين، وغلبة العدو، وشماتة الأعداء", transliteration: "Allahumma inni a'udhu bika min ghalabatid-dayn, wa ghalabatil-'aduww, wa shamatatil-a'da'.", translation: "O Allah, I seek refuge in You from being overcome by debt, being overcome by the enemy, and the gloating of enemies.", source: "(Sahih An-Nasa'i)" },
    { id: 304, category: "prophetic", arabic: "اللهم اغفر لي، واهدني، وارزقني، وعافني، أعوذ بالله من ضيق المقام يوم القيامة", transliteration: "Allahummaghfir li, wahdini, warzuqni, wa 'afini. A'udhu billahi min diqil-maqami yawmal-qiyamah.", translation: "O Allah, forgive me, guide me, provide for me, and heal me. I seek refuge in Allah from the distress of the standing on the Day of Resurrection.", source: "(Sahih An-Nasa'i)" },
    { id: 305, category: "prophetic", arabic: "بِسْمِ اللهِ، تَوَكَّلْتُ عَلَى اللهِ، وَلَا حَوْلَ وَلَا قُوَّةَ إِلَّا بِاللهِ", transliteration: "Bismillahi, tawakkaltu 'alallahi, wa la hawla wa la quwwata illa billah.", translation: "In the Name of Allah, I have placed my trust in Allah; there is no might and no power except by Allah.", source: "(Abu Dawud: 5095, At-Tirmidhi: 3426)" },
];
